mapper.select:
select t4.*, (t4.total_room_count - t4.room_count) as 'free_room_count' from (select t1.*, ifnull(t2.total_room_count,0) as 'total_room_count', ifnull(t3.count_room, 0) as 'room_count' from (select * from apartment_info where is_deleted = 0) t1 left join (select apartment_id, count(id) as total_room_count from room_info where is_deleted = 0 and is_release = 1 group by apartment_id) t2 on t1.id = t2.apartment_id left join (select apartment_id, count(id) as count_room from lease_agreement where is_deleted = 0 and status in (2,5) group by apartment_id) t3 on t1.id = t3.apartment_id) t4
and t4.province_id = #{queryVo.provinceId}
and t4.city_id = #{queryVo.cityId}
and t4.district_id = #{queryVo.districtId}
